INSERT INTO `ycity_course` VALUES ('100', '0', '科技论文写作', '', '', '56', '郭汉丁,黄凌翔,王磊,李文忠,谢爱国', '', 'http://glx.tjuci.edu.cn/kjlwxz/', '<p>&nbsp;科技论文写作是我校经济与管理学院全院各专业（9个）的学科基础课，也可作为全校选修课。课程围绕培养学生&ldquo;学术精神与素养，科研方法与规范，写作实践与能力，创新意识与思维&rdquo;组织教学内容，开展教学活动，实施教学考核，实现教学目的。</p>', '<p>&nbsp;科技论文写作是我校经济与管理学院全院各专业（9个）的学科基础课，也可作为全校选修课。课程围绕培养学生“学术精神与素养，科研方法与规范，写作实践与能力，创新意识与思维”组织教学内容，开展教学活动，实施教学考核，实现教学目的。</p>', '', '1', 'News/201607/578d77ff0b79d.jpg', 'News/201607/2014032402.jpg', '1', '0', '0', '0', '0', '1970', '0');
INSERT INTO `ycity_course` VALUES ('103', '1', '工程招投标与合同管理', '', '', '55', '王建廷、李锦华、任志涛、李德华、张睿', '', 'http://ztb.tjuci.edu.cn/', '', '<p><span style=\"font-family: \" microsoft=\"\" font-size:=\"\" background-color:=\"\">“工程招投标与合同管理”是工程管理专业的专业基础课，是工程管理专业学生的必修课程，同时也是工商管理、市场营销、土地资源管理、土木工程和建筑</span><br /></p>', '', '1', 'Course/201607/579089b42efc6.jpg', 'Course/201607/579089b42efc6_s.jpg', '0', '0', '0', '0', '0', '1970', '0');
INSERT INTO `ycity_course` VALUES ('106', '1', '工程计量与计价', '', '', '56', '李锦华,郝鹏,刘海山,王艳娜,王英', '', 'http://glx.tjuci.edu.cn/jlyjj/index.asp', '', '<p><span style=\"font-family: &quot;Microsoft Yahei&quot;, 宋体, Helvetica, Arial, sans-serif;font-size: 12px;background-color: rgb(249, 249, 249)\">“工程计量与计价”课程是培养学生工程计量与计价理论与实践能力的课程，是工程管理专业的专业核心课。通过本课程的学习，使学生掌握工程造价确定的基本理论和方法，使学生具备...</span><br /></p>', '', '1', 'Course/201607/57908c3a5b8e4.jpg', 'Course/201607/57908c3a5b8e4_s.jpg', '0', '0', '0', '0', '0', '2016', '0');
INSERT INTO `ycity_course` VALUES ('104', '1', '系统工程与运筹学', '', '', '55', '董肇君、郭汉丁、焦爱英、唐智娟、李美岩', '', 'http://glxjp.tjuci.edu.cn/default.asp', '', '<p><span style=\"font-family: \" microsoft=\"\" font-size:=\"\" background-color:=\"\">《系统工程与运筹学》是管理类各专业的重要专业基础课之一。它是按系统科学体系构建的，它是将系统思想与定量技术有机结合，为培养学生管理创新能力提供支撑的方法论课程。</span><br /></p>', '', '1', 'Course/201607/57908a17eaa88.gif', 'Course/201607/57908a17eaa88_s.gif', '0', '0', '0', '0', '0', '1970', '0');
INSERT INTO `ycity_course` VALUES ('105', '1', '应用统计学', '', '', '55', '符啟勋、方晶晶、杜建刚、曹琳剑、李海萍', '', 'http://glx.tjuci.edu.cn/yytjx', '', '<p><span style=\"font-family: &quot;Microsoft Yahei&quot;, 宋体, Helvetica, Arial, sans-serif;font-size: 12px;background-color: rgb(249, 249, 249)\">应用统计学课程是天津城市建设学院管理类专业学生的学科基础课，管理工程系现有的6个专业和辅修专业均开设。主要任务是培养学生收集数据和分析数据的能力，并为后续课程的学习及科学研究奠定基础。课程主要讲授统计</span><br /></p>', '', '1', 'Course/201607/57908a6eed569.jpg', 'Course/201607/57908a6eed569_s.jpg', '0', '0', '0', '0', '0', '2016', '0');
INSERT INTO `ycity_course` VALUES ('107', '1', '工程项目管理', '', '', '56', '郭汉丁,郭伟,王凯,李锦华,马辉', '', 'http://202.113.89.36/gcxmgl', '', '<p><span style=\"font-family: &quot;Microsoft Yahei&quot;, 宋体, Helvetica, Arial, sans-serif;font-size: 12px;background-color: rgb(249, 249, 249)\">工程项目管理是以工程项目为对象，站在建设主体视角，按照系统工程思想构建的一门现代项目管理技术、方法、原理与主要内容；工程项目管理是高等学校工程管理专业指导委员会确定有工程项目</span><br /></p>', '', '1', 'Course/201607/57908c85b385b.jpg', 'Course/201607/57908c85b385b_s.jpg', '0', '0', '0', '0', '0', '2016', '0');
INSERT INTO `ycity_course` VALUES ('108', '1', '管理学原理', '', '', '57', '吕晨,刘红宇,李美岩', '', 'http://glx.tjuci.edu.cn/glxyl/', '', '<p><span style=\"font-family: \" microsoft=\"\" font-size:=\"\" background-color:=\"\">管理学原理是一门系统地研究管理活动的普遍规律和一般方法的科学。它不仅是企业管理各分支领域的基础，更是经济管理类大部分专业必修的核心课程。它综合了经济科学、技术科学、心理学、数学、计算机科学等多门学科的.<span style=\"white-space: normal;\">它不仅是企业管理各分支领域的基础，更是经济管理类大部分专业必修的核心课程。它综合了经济科学、技术科学、心理学、数学、计算机科学等多门学科的</span></span><br /></p>', '', '1', 'Course/201607/57908cc68be67.jpg', 'Course/201607/57908cc68be67_s.jpg', '0', '0', '0', '0', '0', '1970', '0');
