INSERT INTO `ycity_news` VALUES ('78', '58', '1', 'admin', '工程管理专业评估专家组走访校外实习基地', '', '', '', '', '', '', '', '<p><span style=\"font-family: &quot;Microsoft Yahei&quot;, 宋体, Helvetica, Arial, sans-serif;font-size: 12px;background-color: rgb(249, 249, 249)\">&nbsp;&nbsp;&nbsp;</span><st1:chsdate isrocdate=\"False\" islunardate=\"False\" day=\"12\" month=\"5\" year=\"2009\" w:st=\"on\" style=\"font-family: &quot;Microsoft Yahei&quot;, 宋体, Helvetica, Arial, sans-serif; white-space: normal; line-height: 24px; text-indent: 28px; font-size: medium; background-color: rgb(249, 249, 249);\"><span lang=\"EN-US\">5</span>月<span lang=\"EN-US\">12</span>日</st1:chsdate><span style=\"font-size: medium;background-color: rgb(249, 249, 249)\">下午，工程管理专业评估委员会专家组在副院长王建廷教授、教务处处长胡建平教授、系主任郭汉丁教授等领导的陪同下，走访和考察了工程管理专业校外实习基地</span><span lang=\"EN-US\" style=\"font-family: &quot;Microsoft Yahei&quot;, 宋体, Helvetica, Arial, sans-serif;font-size: medium;background-color: rgb(249, 249, 249)\">——</span><span style=\"font-size: medium;background-color: rgb(249, 249, 249)\">天津建工工程总承包有限公司、万科房地产有限公司。实习基地的有关领导对工程管理专业的学生作了中肯的评价，认为学生们思想觉悟高，能够吃苦耐劳，虚心学习，专业基础知识扎实，实际操作能力强，经过专业人员的指导，能够很快适应岗位工作。同时，专家组与在该基地工作的工程管理专业毕业生进行了座谈。</span><br /></p>', '', '', null, null, null, '0', '0', '0', '0', '1', '1468857600', null);
